package week1.day1;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=20;
		
		if (i%2 ==0)
		{
			System.out.println("The given number is Even");
		}		
		else
		{
			System.out.println("The given number is Odd");
		}
	}

}
